mapper.insert:

 SELECT BOARD_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO BOARD_T (
     BOARD_NO
   , TITLE
   , CONTENTS
   , CREATE_DT
   , MODIFY_DT
   , USER_NO  
 ) VALUES (
     #{boardNo}
   , #{title}
   , #{contents}
   , SYSTIMESTAMP
   , SYSTIMESTAMP 
   , #{user.userNo}
 )

INSERT INTO ATTACH_T (
    ATTACH_NO
  , UPLOAD_PATH
  , FILESYSTEM_NAME
  , ORIGINAL_FILENAME
  , DOWNLOAD_COUNT
  , HAS_THUMBNAIL
  , BOARD_NO
) VALUES (
    ATTACH_SEQ.NEXTVAL
  , #{uploadPath}
  , #{filesystemName}
  , #{originalFilename}
  , 0    
  , #{hasThumbnail}
  , #{boardNo}
)

mapper.select:
SELECT COUNT(*)
  FROM COMMENT_T
 WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE COMMENT_T
   SET STATE = 0
 WHERE BOARD_NO = #{boardNo}
